import React, { useState } from 'react';
import { Tabs, Form, Input, Button, Select, message, Alert, Spin } from 'antd';
import { createL1Graph, createL2Graph } from '../services/api';

const { TabPane } = Tabs;
const { Option } = Select;

const GraphCreator = ({ addTask, showNotification }) => {
  const [activeTab, setActiveTab] = useState('l1');
  const [l1Graphs, setL1Graphs] = useState([]);
  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);

  const handleL1Submit = async (values) => {
    setLoading(true);
    try {
      const response = await createL1Graph(values.keyword);
      if (response.success) {
        const task = {
          task_id: response.task_id,
          type: 'L1图谱创建',
          keyword: values.keyword,
          status: 'processing'
        };
        addTask(task);
        
        // 启动轮询
        const finalStatus = await pollTaskStatus(response.task_id);
        updateTask(response.task_id, {
          status: finalStatus.error ? 'failed' : 'completed',
          message: finalStatus.error || '任务完成',
          result: finalStatus.result
        });
        
        message.success('L1图谱创建完成');
      }
    } catch (error) {
      showNotification('error', '创建失败', '创建L1图谱时出错');
    } finally {
      setLoading(false);
    }
  };

  const handleL2Submit = async (values) => {
    setLoading(true);
    try {
      const response = await createL2Graph(values.keyword, values.parentId);
      if (response.success) {
        addTask({
          task_id: response.task_id,
          type: 'L2图谱创建',
          keyword: values.keyword,
          parent_id: values.parentId,
          status: 'processing'
        });
        message.success('L2图谱创建任务已启动');
        form.resetFields();
      } else {
        showNotification('warning', '创建失败', response.message);
      }
    } catch (error) {
      showNotification('error', '创建失败', '创建L2图谱时出错');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <Tabs activeKey={activeTab} onChange={setActiveTab}>
        <TabPane tab="创建L1图谱" key="l1">
          <Form
            form={form}
            layout="vertical"
            onFinish={handleL1Submit}
            style={{ maxWidth: 500, margin: '0 auto' }}
          >
            <Form.Item
              label="学科/领域名称"
              name="keyword"
              rules={[{ required: true, message: '请输入学科或领域名称' }]}
            >
              <Input placeholder="例如: Python编程, 机器学习" />
            </Form.Item>

            <Form.Item>
              <Button type="primary" htmlType="submit" loading={loading}>
                创建图谱
              </Button>
            </Form.Item>
          </Form>

          <Alert
            message="L1图谱创建说明"
            description="L1图谱代表一个完整的学科或知识领域的学习大纲。创建过程可能需要一些时间，请耐心等待。"
            type="info"
            showIcon
            style={{ marginTop: 20 }}
          />
        </TabPane>

        <TabPane tab="创建L2图谱" key="l2">
          <Form
            form={form}
            layout="vertical"
            onFinish={handleL2Submit}
            style={{ maxWidth: 500, margin: '0 auto' }}
          >
            <Form.Item
              label="所属L1图谱ID"
              name="parentId"
              rules={[{ required: true, message: '请输入父级L1图谱ID' }]}
            >
              <Input placeholder="例如: e8a3b1c4-..." />
            </Form.Item>

            <Form.Item
              label="具体知识点"
              name="keyword"
              rules={[{ required: true, message: '请输入要展开的具体知识点' }]}
            >
              <Input placeholder="例如: Python数据结构, 神经网络" />
            </Form.Item>

            <Form.Item>
              <Button type="primary" htmlType="submit" loading={loading}>
                创建图谱
              </Button>
            </Form.Item>
          </Form>

          <Alert
            message="L2图谱创建说明"
            description="L2图谱是L1中某个具体知识点的详细展开。请确保输入的L1图谱ID存在，且知识点属于该L1图谱的内容。"
            type="info"
            showIcon
            style={{ marginTop: 20 }}
          />
        </TabPane>
      </Tabs>
    </div>
  );
};

export default GraphCreator;
