import React, { useState, useRef, useEffect } from 'react';
import {
  Input,
  Button,
  Card,
  List,
  Avatar,
  Select,
  Divider,
  Spin,
  message,
  Form  
} from 'antd';
import { UserOutlined, RobotOutlined } from '@ant-design/icons';
import { streamChat } from '../services/api';

const { TextArea } = Input;
const { Option } = Select;

const LearningChat = ({ showNotification }) => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [loading, setLoading] = useState(false);
  const [contextLevel, setContextLevel] = useState('level1');
  const [contextId, setContextId] = useState(null);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // const handleSendMessage = async () => {
  //   if (!inputValue.trim()) return;
  //
  //   const userMessage = {
  //     content: inputValue,
  //     sender: 'user',
  //     timestamp: new Date().toISOString()
  //   };
  //
  //   setMessages([...messages, userMessage]);
  //   setInputValue('');
  //   setLoading(true);
  //
  //   try {
  //     const botResponse = await streamChat(inputValue, contextLevel, contextId);
  //
  //     setMessages(prev => [
  //       ...prev,
  //       {
  //         content: botResponse.content,
  //         sender: 'bot',
  //         timestamp: new Date().toISOString()
  //       }
  //     ]);
  //   } catch (error) {
  //     showNotification('error', '发送失败', '与AI导师通信时出错');
  //   } finally {
  //     setLoading(false);
  //   }
  // };

  const handleSendMessage = async () => {
  if (!inputValue.trim()) return;

  const userMessage = {
    content: inputValue,
    sender: 'user',
    timestamp: new Date().toISOString()
  };

  setMessages([...messages, userMessage]);
  setInputValue('');
  setLoading(true);

  try {
    // 添加一个临时消息用于显示流式响应
    setMessages(prev => [...prev, {
      content: '',
      sender: 'bot',
      timestamp: new Date().toISOString(),
      isStreaming: true
    }]);

    const response = await streamChat(inputValue, contextLevel, contextId);

    // 处理流式数据（假设使用EventSource或其他流式处理方式）
    // 这里需要根据实际的流式实现进行调整
    let content = '';
    response.on('data', (chunk) => {
      content += chunk;
      setMessages(prev => {
        const last = prev[prev.length - 1];
        if (last.isStreaming) {
          return [...prev.slice(0, -1), { ...last, content }];
        }
        return prev;
      });
    });

    response.on('end', () => {
      setMessages(prev => {
        const last = prev[prev.length - 1];
        if (last.isStreaming) {
          return [...prev.slice(0, -1), { ...last, isStreaming: false }];
        }
        return prev;
      });
      setLoading(false);
    });
  } catch (error) {
    setMessages(prev => [...prev, {
      content: '抱歉，与AI导师通信时出错',
      sender: 'bot',
      timestamp: new Date().toISOString(),
      isError: true
    }]);
    setLoading(false);
  }
};

// 在UI中添加contextId输入
<Form.Item label="上下文ID" style={{ marginBottom: 8 }}>
  <Input
    value={contextId || ''}
    onChange={(e) => setContextId(e.target.value || null)}
    placeholder="输入知识图谱ID (可选)"
  />
</Form.Item>

  return (
    <div style={{ maxWidth: 800, margin: '0 auto' }}>
      <Card
        title="AI学习导师"
        extra={
          <Select
            defaultValue="level1"
            style={{ width: 120 }}
            onChange={setContextLevel}
          >
            <Option value="level1">Level 1 上下文</Option>
            <Option value="level2">Level 2 上下文</Option>
          </Select>
        }
      >
        <div style={{ height: 500, overflowY: 'auto', marginBottom: 16 }}>
          <List
            dataSource={messages}
            renderItem={(item) => (
              <List.Item style={{
                padding: '12px 0',
                justifyContent: item.sender === 'user' ? 'flex-end' : 'flex-start'
              }}>
                <div style={{
                  display: 'flex',
                  flexDirection: item.sender === 'user' ? 'row-reverse' : 'row',
                  alignItems: 'flex-start',
                  gap: 12
                }}>
                  <Avatar
                    icon={item.sender === 'user' ? <UserOutlined /> : <RobotOutlined />}
                    style={{
                      backgroundColor: item.sender === 'user' ? '#1890ff' : '#52c41a'
                    }}
                  />
                  <div style={{
                    maxWidth: '70%',
                    backgroundColor: item.sender === 'user' ? '#e6f7ff' : '#f6ffed',
                    padding: '8px 12px',
                    borderRadius: 4
                  }}>
                    {item.content}
                  </div>
                </div>
              </List.Item>
            )}
          />
          <div ref={messagesEndRef} />
          {loading && (
            <div style={{ textAlign: 'center', padding: 12 }}>
              <Spin tip="AI导师正在思考..." />
            </div>
          )}
        </div>

        <div style={{ display: 'flex', gap: 8 }}>
          <TextArea
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            placeholder="输入您的问题..."
            autoSize={{ minRows: 2, maxRows: 6 }}
            onPressEnter={(e) => {
              if (!e.shiftKey) {
                e.preventDefault();
                handleSendMessage();
              }
            }}
          />
          <Button
            type="primary"
            onClick={handleSendMessage}
            style={{ height: 'auto' }}
          >
            发送
          </Button>
        </div>

        <Divider />
        <div>
          <h4>示例问题:</h4>
          <p>- "请解释Python中的列表和元组的区别"</p>
          <p>- "机器学习中的监督学习和无监督学习有什么不同?"</p>
          <p>- "世界历史中的工业革命有哪些重要影响?"</p>
        </div>
      </Card>
    </div>
  );
};

export default LearningChat;
