import React, { useState, useRef, useEffect } from 'react';
import {
  Input,
  Button,
  Card,
  List,
  Avatar,
  Select,
  Divider,
  Spin,
  message,
  Form,
  Typography
} from 'antd';
import { UserOutlined, RobotOutlined, SendOutlined } from '@ant-design/icons';
import { streamChat } from '../services/api';

const { TextArea } = Input;
const { Option } = Select;
const { Item } = Form;
const { Title } = Typography;

const LearningChat = ({ showNotification }) => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [loading, setLoading] = useState(false);
  const [contextLevel, setContextLevel] = useState('level1');
  const [contextId, setContextId] = useState(null);
  const messagesEndRef = useRef(null);
  const [form] = Form.useForm();

  // 自动滚动到底部
  // useEffect(() => {
  //   messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  // }, [messages]);

  const handleSendMessage = async () => {
    if (!inputValue.trim()) return;

    const userMessage = {
      content: inputValue,
      sender: 'user',
      timestamp: new Date().toISOString()
    };

    setMessages([...messages, userMessage]);
    setInputValue('');
    setLoading(true);

    try {
      setMessages(prev => [...prev, {
        content: '...',
        sender: 'bot',
        timestamp: new Date().toISOString(),
        isStreaming: true
      }]);

      const response = await streamChat(inputValue, contextLevel, contextId);

      setMessages(prev => {
        const last = prev[prev.length - 1];
        if (last.isStreaming) {
          return [...prev.slice(0, -1), {
            ...last,
            content: response.content || response,
            isStreaming: false
          }];
        }
        return prev;
      });
    } catch (error) {
      showNotification('error', '发送失败', error.message);
      setMessages(prev => [...prev, {
        content: '抱歉，与AI导师通信时出错',
        sender: 'bot',
        timestamp: new Date().toISOString(),
        isError: true
      }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      maxWidth: '900px',
      margin: '0 auto',
      padding: '24px 16px',
      height: '100vh',
      display: 'flex',
      flexDirection: 'column'
    }}>
      <Card
        title={
          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between'
          }}>
            <Title level={4} style={{
              margin: 0,
              background: 'linear-gradient(90deg, #2a97ff, #0066cc)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent'
            }}>
              AI学习导师
            </Title>
            <Select
              defaultValue="level1"
              style={{ width: 160 }}
              onChange={setContextLevel}
              dropdownStyle={{
                borderRadius: '12px',
                boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
              }}
            >
              <Option value="level1">基础上下文</Option>
              <Option value="level2">高级上下文</Option>
            </Select>
          </div>
        }
        headStyle={{
          borderBottom: '1px solid rgba(0, 0, 0, 0.05)',
          padding: '16px 24px',
          background: 'rgba(255, 255, 255, 0.8)',
          backdropFilter: 'blur(8px)',
          borderRadius: '12px 12px 0 0'
        }}
        bodyStyle={{
          padding: 0,
          background: 'linear-gradient(135deg, #f8fafc, #f1f5f9)',
          borderRadius: '0 0 12px 12px'
        }}
        style={{
          borderRadius: '12px',
          boxShadow: '0 8px 24px rgba(42, 151, 255, 0.15)',
          border: 'none',
          flex: 1,
          display: 'flex',
          flexDirection: 'column'
        }}
      >
        <div style={{
          flex: 1,
          overflowY: 'auto',
          padding: '24px',
          background: 'linear-gradient(135deg, #f8fafc, #f1f5f9)'
        }}>
          <List
            dataSource={messages}
            renderItem={(item) => (
              <List.Item style={{
                padding: '8px 0',
                justifyContent: item.sender === 'user' ? 'flex-end' : 'flex-start',
                animation: 'fadeIn 0.3s ease-out'
              }}>
                <div style={{
                  display: 'flex',
                  flexDirection: item.sender === 'user' ? 'row-reverse' : 'row',
                  alignItems: 'flex-start',
                  gap: 12,
                  maxWidth: '85%'
                }}>
                  <Avatar
                    icon={item.sender === 'user' ? <UserOutlined /> : <RobotOutlined />}
                    style={{
                      backgroundColor: item.sender === 'user' ? '#2a97ff' : '#00b4b4',
                      boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
                      flexShrink: 0
                    }}
                  />
                  <div style={{
                    background: item.sender === 'user'
                      ? 'linear-gradient(135deg, #2a97ff, #0066cc)'
                      : 'linear-gradient(135deg, #ffffff, #f8fafc)',
                    color: item.sender === 'user' ? 'white' : '#333',
                    padding: '12px 16px',
                    borderRadius: item.sender === 'user' ? '18px 4px 18px 18px' : '4px 18px 18px 18px',
                    boxShadow: '0 2px 12px rgba(0, 0, 0, 0.08)',
                    transition: 'all 0.3s',
                    border: item.sender === 'bot' ? '1px solid rgba(0, 0, 0, 0.05)' : 'none',
                    position: 'relative',
                    overflow: 'hidden'
                  }}>
                    {item.isStreaming && (
                      <div style={{
                        position: 'absolute',
                        bottom: 0,
                        left: 0,
                        right: 0,
                        height: '2px',
                        background: 'linear-gradient(90deg, transparent, rgba(42, 151, 255, 0.5), transparent)',
                        animation: 'streaming 1.5s infinite'
                      }} />
                    )}
                    {item.content}
                  </div>
                </div>
              </List.Item>
            )}
          />
          <div ref={messagesEndRef} />
          {loading && (
            <div style={{
              textAlign: 'center',
              padding: 16,
              display: 'flex',
              justifyContent: 'center'
            }}>
              <div style={{
                background: 'white',
                padding: '12px 24px',
                borderRadius: '24px',
                boxShadow: '0 4px 12px rgba(0, 0, 0, 0.05)',
                display: 'inline-flex',
                alignItems: 'center',
                gap: '8px'
              }}>
                <Spin size="small" />
                <span style={{ color: '#666' }}>AI导师正在思考...</span>
              </div>
            </div>
          )}
        </div>

        <div style={{
          padding: '16px 24px',
          borderTop: '1px solid rgba(0, 0, 0, 0.05)',
          background: 'white'
        }}>
          <Form form={form} layout="vertical">
            <Item label="上下文ID" name="contextId" style={{ marginBottom: 12 }}>
              <Input
                placeholder="输入知识图谱ID (可选)"
                onChange={(e) => setContextId(e.target.value || null)}
                style={{
                  borderRadius: '8px',
                  padding: '8px 12px'
                }}
              />
            </Item>
            <Item>
              <div style={{
                display: 'flex',
                gap: 12,
                alignItems: 'flex-end'
              }}>
                <TextArea
                  value={inputValue}
                  onChange={(e) => setInputValue(e.target.value)}
                  placeholder="输入您的问题..."
                  autoSize={{ minRows: 1, maxRows: 4 }}
                  onPressEnter={(e) => {
                    if (!e.shiftKey) {
                      e.preventDefault();
                      handleSendMessage();
                    }
                  }}
                  style={{
                    borderRadius: '12px',
                    padding: '12px 16px',
                    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.05)',
                    border: '1px solid rgba(0, 0, 0, 0.1)',
                    transition: 'all 0.3s',
                    flex: 1
                  }}
                />
                <Button
                  type="primary"
                  onClick={handleSendMessage}
                  style={{
                    height: '44px',
                    width: '44px',
                    borderRadius: '12px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    background: 'linear-gradient(135deg, #2a97ff, #0066cc)',
                    border: 'none',
                    boxShadow: '0 4px 12px rgba(42, 151, 255, 0.3)',
                    transition: 'all 0.3s'
                  }}
                  loading={loading}
                  disabled={loading}
                >
                  <SendOutlined style={{ fontSize: '18px' }} />
                </Button>
              </div>
            </Item>
          </Form>
        </div>
      </Card>

      <style jsx global>{`
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }
        @keyframes streaming {
          0% { transform: translateX(-100%); }
          100% { transform: translateX(100%); }
        }
      `}</style>
    </div>
  );
};

export default LearningChat;
