import React, { useState } from 'react';
import { Tabs, Form, Input, Button, Select, message, Alert, Spin, Card, Space, Typography } from 'antd';
import { createL1Graph, createL2Graph, pollTaskStatus } from '../services/api';
import { RocketOutlined, BookOutlined, PlusOutlined } from '@ant-design/icons';
import styled, { keyframes } from 'styled-components';

const { TabPane } = Tabs;
const { Option } = Select;
const { Title, Text } = Typography;

// 动画定义
const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(10px); }
  to { opacity: 1; transform: translateY(0); }
`;

const pulse = keyframes`
  0% { box-shadow: 0 0 0 0 rgba(24, 144, 255, 0.4); }
  70% { box-shadow: 0 0 0 10px rgba(24, 144, 255, 0); }
  100% { box-shadow: 0 0 0 0 rgba(24, 144, 255, 0); }
`;

// 样式组件
const StyledCard = styled(Card)`
  border-radius: 12px;
  border: none;
  background: linear-gradient(145deg, #ffffff, #f5f9ff);
  box-shadow: 0 8px 32px rgba(24, 144, 255, 0.1);
  overflow: hidden;
  transition: all 0.3s ease;
  
  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 12px 40px rgba(24, 144, 255, 0.15);
  }
  
  .ant-card-head {
    border-bottom: 1px solid rgba(24, 144, 255, 0.1);
    background: linear-gradient(90deg, #1890ff, #36cffa);
    color: white;
    
    .ant-card-head-title {
      padding: 16px 0;
    }
  }
`;

const StyledTabs = styled(Tabs)`
  .ant-tabs-nav {
    margin-bottom: 24px;
    
    &::before {
      border-bottom: 1px solid rgba(24, 144, 255, 0.1);
    }
    
    .ant-tabs-tab {
      padding: 12px 24px;
      transition: all 0.3s ease;
      
      &:hover {
        color: #1890ff;
      }
      
      &.ant-tabs-tab-active {
        .ant-tabs-tab-btn {
          color: #1890ff;
          text-shadow: none;
        }
      }
    }
    
    .ant-tabs-ink-bar {
      background: linear-gradient(90deg, #1890ff, #36cffa);
      height: 3px;
    }
  }
`;

const StyledFormItem = styled(Form.Item)`
  margin-bottom: 20px;
  
  .ant-form-item-label > label {
    color: #3a5169;
    font-weight: 500;
  }
`;

const StyledInput = styled(Input)`
  border-radius: 8px;
  padding: 12px 16px;
  border: 1px solid #e6f0ff;
  transition: all 0.3s ease;
  
  &:hover {
    border-color: #b3d4ff;
  }
  
  &:focus {
    border-color: #1890ff;
    box-shadow: 0 0 0 2px rgba(24, 144, 255, 0.2);
  }
`;

const PrimaryButton = styled(Button)`
  border-radius: 8px;
  padding: 12px 24px;
  height: auto;
  font-weight: 500;
  background: linear-gradient(90deg, #1890ff, #36cffa);
  border: none;
  transition: all 0.3s ease;
  animation: ${fadeIn} 0.5s ease;
  
  &:hover {
    background: linear-gradient(90deg, #1483f0, #2bb8e6);
    transform: translateY(-1px);
    box-shadow: 0 4px 12px rgba(24, 144, 255, 0.3);
  }
  
  &:active {
    transform: translateY(0);
  }
  
  &.ant-btn-loading {
    animation: ${pulse} 1.5s infinite;
  }
`;

const InfoAlert = styled(Alert)`
  border-radius: 8px;
  border: 1px solid #e6f7ff;
  background-color: #f6fbff;
  animation: ${fadeIn} 0.6s ease;
  
  .ant-alert-message {
    color: #3a5169;
    font-weight: 500;
  }
  
  .ant-alert-description {
    color: #6a7c8f;
  }
`;

const GraphCreator = ({ addTask, showNotification }) => {
  const [activeTab, setActiveTab] = useState('l1');
  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleL1Submit = async (values) => {
    setIsSubmitting(true);
    setLoading(true);
    try {
      const response = await createL1Graph(values.keyword);
      if (response.success) {
        const task = {
          task_id: response.task_id,
          type: 'L1图谱创建',
          keyword: values.keyword,
          status: 'processing'
        };
        addTask(task);

        const finalStatus = await pollTaskStatus(response.task_id);
        updateTask(response.task_id, {
          status: finalStatus.error ? 'failed' : 'completed',
          message: finalStatus.error || '任务完成',
          result: finalStatus.result
        });

        message.success({
          content: 'L1图谱创建完成!',
          icon: <RocketOutlined style={{ color: '#52c41a' }} />,
        });
        form.resetFields();
      }
    } catch (error) {
      showNotification('error', '创建失败', '创建L1图谱时出错');
    } finally {
      setLoading(false);
      setIsSubmitting(false);
    }
  };

  const handleL2Submit = async (values) => {
    setIsSubmitting(true);
    setLoading(true);
    try {
      const response = await createL2Graph(values.keyword, values.parentId);
      if (response.success) {
        addTask({
          task_id: response.task_id,
          type: 'L2图谱创建',
          keyword: values.keyword,
          parent_id: values.parentId,
          status: 'processing'
        });
        message.success({
          content: 'L2图谱创建任务已启动!',
          icon: <RocketOutlined style={{ color: '#52c41a' }} />,
        });
        form.resetFields();
      } else {
        showNotification('warning', '创建失败', response.message);
      }
    } catch (error) {
      showNotification('error', '创建失败', '创建L2图谱时出错');
    } finally {
      setLoading(false);
      setIsSubmitting(false);
    }
  };

  return (
    <StyledCard
      title={
        <Space>
          <PlusOutlined style={{ color: 'white' }} />
          <Title level={4} style={{ margin: 0, color: 'white' }}>知识图谱创建</Title>
        </Space>
      }
    >
      <StyledTabs
        activeKey={activeTab}
        onChange={setActiveTab}
        animated={{ inkBar: true, tabPane: true }}
      >
        <TabPane
          tab={
            <span>
              <BookOutlined />
              <span style={{ marginLeft: 8 }}>创建L1图谱</span>
            </span>
          }
          key="l1"
        >
          <div style={{ maxWidth: 600, margin: '0 auto' }}>
            <Form
              form={form}
              layout="vertical"
              onFinish={handleL1Submit}
            >
              <StyledFormItem
                label={<Text strong>学科/领域名称</Text>}
                name="keyword"
                rules={[{ required: true, message: '请输入学科或领域名称' }]}
              >
                <StyledInput
                  placeholder="例如: Python编程, 机器学习"
                  size="large"
                  allowClear
                />
              </StyledFormItem>

              <StyledFormItem>
                <PrimaryButton
                  type="primary"
                  htmlType="submit"
                  loading={isSubmitting}
                  size="large"
                  style={{ width: '100%' }}
                  icon={<RocketOutlined />}
                >
                  {isSubmitting ? '创建中...' : '创建L1图谱'}
                </PrimaryButton>
              </StyledFormItem>
            </Form>

            <InfoAlert
              message={<Text strong>L1图谱创建说明</Text>}
              description="L1图谱代表一个完整的学科或知识领域的学习大纲。创建过程可能需要一些时间，请耐心等待。"
              type="info"
              showIcon
              style={{ marginTop: 24 }}
            />
          </div>
        </TabPane>

        <TabPane
          tab={
            <span>
              <BookOutlined />
              <span style={{ marginLeft: 8 }}>创建L2图谱</span>
            </span>
          }
          key="l2"
        >
          <div style={{ maxWidth: 600, margin: '0 auto' }}>
            <Form
              form={form}
              layout="vertical"
              onFinish={handleL2Submit}
            >
              <StyledFormItem
                label={<Text strong>所属L1图谱ID</Text>}
                name="parentId"
                rules={[{ required: true, message: '请输入父级L1图谱ID' }]}
              >
                <StyledInput
                  placeholder="例如: e8a3b1c4-..."
                  size="large"
                  allowClear
                />
              </StyledFormItem>

              <StyledFormItem
                label={<Text strong>具体知识点</Text>}
                name="keyword"
                rules={[{ required: true, message: '请输入要展开的具体知识点' }]}
              >
                <StyledInput
                  placeholder="例如: Python数据结构, 神经网络"
                  size="large"
                  allowClear
                />
              </StyledFormItem>

              <StyledFormItem>
                <PrimaryButton
                  type="primary"
                  htmlType="submit"
                  loading={isSubmitting}
                  size="large"
                  style={{ width: '100%' }}
                  icon={<RocketOutlined />}
                >
                  {isSubmitting ? '创建中...' : '创建L2图谱'}
                </PrimaryButton>
              </StyledFormItem>
            </Form>

            <InfoAlert
              message={<Text strong>L2图谱创建说明</Text>}
              description="L2图谱是L1中某个具体知识点的详细展开。请确保输入的L1图谱ID存在，且知识点属于该L1图谱的内容。"
              type="info"
              showIcon
              style={{ marginTop: 24 }}
            />
          </div>
        </TabPane>
      </StyledTabs>
    </StyledCard>
  );
};

export default GraphCreator;
