import React, { useState } from 'react';
import { Layout, Menu, Spin, notification } from 'antd';
import {
  BookOutlined,
  PlusOutlined,
  MessageOutlined,
  DashboardOutlined
} from '@ant-design/icons';
import GraphBrowser from './components/GraphBrowser';
import GraphCreator from './components/GraphCreator';
import LearningChat from './components/LearningChat';
import TaskMonitor from './components/TaskMonitor';
import './index.css';

const { Header, Content } = Layout;

function App() {
  const [activeTab, setActiveTab] = useState('browse');
  const [loading, setLoading] = useState(false);
  const [tasks, setTasks] = useState([]);

  const addTask = (task) => {
    setTasks([...tasks, task]);
  };

  const updateTask = (taskId, updates) => {
    setTasks(tasks.map(task =>
      task.task_id === taskId ? { ...task, ...updates } : task
    ));
  };

  const showNotification = (type, message, description) => {
    notification[type]({
      message,
      description,
      placement: 'bottomRight'
    });
  };

  return (
    <Layout style={{ minHeight: '100vh', fontFamily: "'Segoe UI', 'PingFang SC', sans-serif" }}>
      <Header className="header" style={{
        background: '#2a97ff',
        padding: '0 24px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <div className="logo" style={{
          color: '#fff',
          fontSize: '20px',
          fontWeight: '600',
          marginRight: '40px'
        }}>
          自适应学习路径规划系统
        </div>
        <Menu
          theme="dark"
          mode="horizontal"
          style={{
            flex: 1,
            background: 'transparent',
            borderBottom: 'none',
            fontSize: '15px',
            display: 'flex',
            justifyContent: 'flex-start'
          }}
          onSelect={({ key }) => setActiveTab(key)}
        >
          <Menu.Item
            key="browse"
            icon={<BookOutlined style={{ fontSize: '18px' }} />}
            style={{
              margin: '0 20px',
              color: '#e6f7ff',
              fontWeight: '500',
              padding: '0 15px'
            }}
          >
            浏览知识图谱
          </Menu.Item>
          <Menu.Item
            key="create"
            style={{
              margin: '0 20px',
              color: '#e6f7ff',
              fontWeight: '500',
              padding: '0 15px'
            }}
          >
            创建新图谱
          </Menu.Item>
          <Menu.Item
            key="learn"
            icon={<MessageOutlined style={{ fontSize: '18px' }} />}
            style={{
              margin: '0 20px',
              color: '#e6f7ff',
              fontWeight: '500',
              padding: '0 15px'
            }}
          >
            学习对话
          </Menu.Item>
          <Menu.Item
            key="tasks"
            icon={<DashboardOutlined style={{ fontSize: '18px' }} />}
            style={{
              margin: '0 20px',
              color: '#e6f7ff',
              fontWeight: '500',
              padding: '0 15px'
            }}
          >
            任务监控
          </Menu.Item>
        </Menu>
      </Header>
      <Layout style={{ background: '#f0f2f5' }}>
        <Content
          className="site-layout-background"
          style={{
            padding: '16px 24px',
            margin: 0,
            minHeight: 'calc(100vh - 64px)',
            background: '#fff',
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.09)'
          }}
        >
          {loading ? (
            <div style={{ textAlign: 'center', padding: '30px' }}>
              <Spin size="large" />
            </div>
          ) : (
            <>
              {activeTab === 'browse' && <GraphBrowser showNotification={showNotification} />}
              {activeTab === 'create' && (
                <GraphCreator
                  addTask={addTask}
                  showNotification={showNotification}
                />
              )}
              {activeTab === 'learn' && (
                <div style={{ height: '70vh' }}>
                  <LearningChat showNotification={showNotification} />
                </div>
              )}
              {activeTab === 'tasks' && (
                <TaskMonitor
                  tasks={tasks}
                  updateTask={updateTask}
                  showNotification={showNotification}
                />
              )}
            </>
          )}
        </Content>
      </Layout>
    </Layout>
  );
}

export default App;
