import React, { useState } from 'react';
import { Layout, Menu, Spin, notification, theme } from 'antd';
import {
  BookOutlined,
  PlusOutlined,
  MessageOutlined,
  DashboardOutlined,
  RocketOutlined
} from '@ant-design/icons';
import GraphBrowser from './components/GraphBrowser';
import GraphCreator from './components/GraphCreator';
import LearningChat from './components/LearningChat';
import TaskMonitor from './components/TaskMonitor';
import './index.css';

const { Header, Content, Sider } = Layout;

function App() {
  const [activeTab, setActiveTab] = useState('browse');
  const [loading, setLoading] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [collapsed, setCollapsed] = useState(false);
  const {
    token: { colorBgContainer, colorPrimary },
  } = theme.useToken();

  const addTask = (task) => {
    setTasks([...tasks, task]);
  };

  const updateTask = (taskId, updates) => {
    setTasks(tasks.map(task =>
      task.task_id === taskId ? { ...task, ...updates } : task
    ));
  };

  const showNotification = (type, message, description) => {
    notification[type]({
      message,
      description,
      placement: 'bottomRight',
      duration: 3,
    });
  };

  return (
    <Layout className="app-layout" style={{ minHeight: '100vh' }}>
      <Header className="app-header">
        <div className="logo-container">
          <RocketOutlined className="logo-icon" />
          <h1 className="logo-text">自适应学习路径规划系统</h1>
        </div>
      </Header>
      <Layout>
        <Sider
          width={220}
          className="app-sider"
          collapsible
          collapsed={collapsed}
          onCollapse={(value) => setCollapsed(value)}
          breakpoint="lg"
          collapsedWidth="80"
        >
          <div className="sider-logo">
            {!collapsed ? (
              <span>知识图谱系统</span>
            ) : (
              <RocketOutlined style={{ fontSize: '20px' }} />
            )}
          </div>
          <Menu
            theme="dark"
            mode="inline"
            selectedKeys={[activeTab]}
            className="app-menu"
            onSelect={({ key }) => setActiveTab(key)}
          >
            <Menu.Item key="browse" icon={<BookOutlined />} className="menu-item">
              {!collapsed && '浏览知识图谱'}
            </Menu.Item>
            <Menu.Item key="create" icon={<PlusOutlined />} className="menu-item">
              {!collapsed && '创建新图谱'}
            </Menu.Item>
            <Menu.Item key="learn" icon={<MessageOutlined />} className="menu-item">
              {!collapsed && '学习对话'}
            </Menu.Item>
            <Menu.Item key="tasks" icon={<DashboardOutlined />} className="menu-item">
              {!collapsed && '任务监控'}
            </Menu.Item>
          </Menu>
        </Sider>
        <Layout className="app-content-layout" style={{
          marginLeft: collapsed ? 80 : 220,
          transition: 'all 0.2s'
        }}>
          <Content
            className="app-content"
            style={{
              background: colorBgContainer,
            }}
          >
            {loading ? (
              <div className="loading-container">
                <Spin size="large" tip="加载中..." />
              </div>
            ) : (
              <div className="content-container">
                {activeTab === 'browse' && <GraphBrowser showNotification={showNotification} />}
                {activeTab === 'create' && (
                  <GraphCreator
                    addTask={addTask}
                    showNotification={showNotification}
                  />
                )}
                {activeTab === 'learn' && <LearningChat showNotification={showNotification} />}
                {activeTab === 'tasks' && (
                  <TaskMonitor
                    tasks={tasks}
                    updateTask={updateTask}
                    showNotification={showNotification}
                  />
                )}
              </div>
            )}
          </Content>
        </Layout>
      </Layout>
    </Layout>
  );
}

export default App;
