import React, { useState } from 'react';
import { Layout, Menu, Spin, notification, theme } from 'antd';
import {
  BookOutlined,
  PlusOutlined,
  MessageOutlined,
  DashboardOutlined,
  RocketOutlined
} from '@ant-design/icons';
import GraphBrowser from './components/GraphBrowser';
import GraphCreator from './components/GraphCreator';
import LearningChat from './components/LearningChat';
import TaskMonitor from './components/TaskMonitor';
import './index.css';

const { Header, Content, Sider } = Layout;

function App() {
  const [activeTab, setActiveTab] = useState('browse');
  const [loading, setLoading] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [collapsed, setCollapsed] = useState(false);
  const {
    token: { colorBgContainer, colorPrimary },
  } = theme.useToken();

  const addTask = (task) => {
    setTasks([...tasks, { ...task, createdAt: Date.now() }]);
  };

  const updateTask = (taskId, updates) => {
    setTasks(tasks.map(task =>
      task.task_id === taskId ? { ...task, ...updates, updatedAt: Date.now() } : task
    ));
  };

  const showNotification = (type, message, description) => {
    notification[type]({
      message,
      description,
      placement: 'bottomRight',
      style: {
        borderRadius: 8,
      }
    });
  };

  return (
    <Layout style={{ minHeight: '100vh' }} className="app-layout">
      <Header className="app-header">
        <div className="logo-container">
          <RocketOutlined className="logo-icon" />
          <h1 className="logo-text">自适应学习路径规划系统</h1>
        </div>
      </Header>
      <Layout>
        <Sider
          width={240}
          collapsible
          collapsed={collapsed}
          onCollapse={(value) => setCollapsed(value)}
          className="app-sider"
          breakpoint="lg"
        >
          <div className="sider-logo">
            {!collapsed && <span>知识图谱系统</span>}
          </div>
          <Menu
            theme="dark"
            mode="inline"
            selectedKeys={[activeTab]}
            onSelect={({ key }) => setActiveTab(key)}
            className="app-menu"
            items={[
              {
                key: 'browse',
                icon: <BookOutlined />,
                label: '浏览知识图谱',
                className: 'menu-item'
              },
              {
                key: 'create',
                icon: <PlusOutlined />,
                label: '创建新图谱',
                className: 'menu-item'
              },
              {
                key: 'learn',
                icon: <MessageOutlined />,
                label: '学习对话',
                className: 'menu-item'
              },
              {
                key: 'tasks',
                icon: <DashboardOutlined />,
                label: '任务监控',
                className: 'menu-item'
              }
            ]}
          />
        </Sider>
        <Layout className="app-content-layout">
          <Content className="app-content">
            {loading ? (
              <div className="loading-container">
                <Spin
                  size="large"
                  tip="加载中..."
                  indicator={
                    <RocketOutlined
                      style={{
                        fontSize: 32,
                        color: colorPrimary,
                        animation: 'rocketSpin 1.5s infinite linear'
                      }}
                    />
                  }
                />
              </div>
            ) : (
              <div className="content-container">
                {activeTab === 'browse' && <GraphBrowser showNotification={showNotification} />}
                {activeTab === 'create' && (
                  <GraphCreator
                    addTask={addTask}
                    showNotification={showNotification}
                  />
                )}
                {activeTab === 'learn' && <LearningChat showNotification={showNotification} />}
                {activeTab === 'tasks' && (
                  <TaskMonitor
                    tasks={tasks}
                    updateTask={updateTask}
                    showNotification={showNotification}
                  />
                )}
              </div>
            )}
          </Content>
        </Layout>
      </Layout>
    </Layout>
  );
}

export default App;
