import React, { useState, useEffect } from 'react';
import { Card, List, Tree, Button, Input, message, Modal, Divider } from 'antd';
import { listL1Graphs, getL1Graph, getL2Graph } from '../services/api';

const { Search } = Input;

const GraphBrowser = ({ showNotification }) => {
  const [l1Graphs, setL1Graphs] = useState([]);
  const [selectedGraph, setSelectedGraph] = useState(null);
  const [l2Graph, setL2Graph] = useState(null);
  const [loading, setLoading] = useState(false);
  const [searchKeyword, setSearchKeyword] = useState('');

  useEffect(() => {
    fetchL1Graphs();
  }, []);

  //获取全部L1知识图谱
  const fetchL1Graphs = async () => {
    setLoading(true);
    try {
      const data = await listL1Graphs();
      setL1Graphs(data);
    } catch (error) {
      showNotification('error', '加载失败', '无法获取L1图谱列表');
    } finally {
      setLoading(false);
    }
  };

  //搜索L1知识图谱
  const handleSearch = async (value) => {
    setLoading(true);
    try {
      const data = await getL1Graph(value);
      if (data.hit) {
        setSelectedGraph({
          id: 'search-result',
          name: value,
          content: data.graph
        });
      } else {
        message.info(`未找到与"${value}"相关的图谱`);
      }
    } catch (error) {
      showNotification('error', '搜索失败', '搜索图谱时出错');
    } finally {
      setLoading(false);
    }
  };

  const handleGraphClick = (graph) => {
    setSelectedGraph(graph);
    setL2Graph(null);
  };

  const handleL2ItemClick = async (item) => {
    setLoading(true);
    try {
      const data = await getL2Graph(item);
      if (data.hit) {
        setL2Graph({
          name: item,
          content: data.graph
        });
      } else {
        message.info(`未找到"${item}"的详细图谱`);
      }
    } catch (error) {
      showNotification('error', '加载失败', '获取L2图谱时出错');
    } finally {
      setLoading(false);
    }
  };

  const parseGraphContent = (content) => {
    return content.split('\n').filter(item => item.trim());
  };

  const renderTreeNodes = (content) => {
    return parseGraphContent(content).map((item, index) => ({
      title: (
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <span>{item}</span>
          <Button
            type="link"
            size="small"
            onClick={(e) => {
              e.stopPropagation();
              handleL2ItemClick(item);
            }}
          >
            查看详情
          </Button>
        </div>
      ),
      key: `${index}`,
      isLeaf: false
    }));
  };

  return (
    <div>
      <Search
        placeholder="搜索知识图谱"
        allowClear
        enterButton="搜索"
        size="large"
        value={searchKeyword}
        onChange={(e) => setSearchKeyword(e.target.value)}
        onSearch={handleSearch}
        style={{ marginBottom: 20 }}
      />

      <div style={{ display: 'flex', gap: 20 }}>
        <div style={{ flex: 1 }}>
          <Card title="L1知识图谱列表" loading={loading}>
            <List
              dataSource={l1Graphs}
              renderItem={(item) => (
                <List.Item
                  onClick={() => handleGraphClick(item)}
                  style={{
                    cursor: 'pointer',
                    backgroundColor: selectedGraph?.id === item.id ? '#ffffff' : 'transparent',
                    padding: '10px'
                  }}
                >
                  <List.Item.Meta
                    title={item.name}
                    description={`创建于 ${new Date(item.created_at * 1000).toLocaleString()}`}
                  />
                </List.Item>
              )}
            />
          </Card>
        </div>

        <div style={{ flex: 2 }}>
          {selectedGraph ? (
            <Card
              title={`L1图谱: ${selectedGraph.name}`}
              extra={<Button type="primary">以此为主题学习</Button>}
              loading={loading}
            >
              <Tree
                treeData={renderTreeNodes(selectedGraph.content)}
                defaultExpandAll
              />
            </Card>
          ) : (
            <Card title="请选择一个L1图谱或进行搜索">
              <div style={{ textAlign: 'center', padding: '20px' }}>
                点击左侧图谱或使用上方搜索框
              </div>
            </Card>
          )}
        </div>

        <div style={{ flex: 2 }}>
          {l2Graph ? (
            <Card title={`L2图谱: ${l2Graph.name}`} loading={loading}>
              <pre style={{ whiteSpace: 'pre-wrap', fontFamily: 'inherit' }}>
                {l2Graph.content}
              </pre>
            </Card>
          ) : (
            <Card title="L2详细知识图谱">
              <div style={{ textAlign: 'center', padding: '20px' }}>
                点击L1图谱中的项目查看详细内容
              </div>
            </Card>
          )}
        </div>
      </div>
    </div>
  );
};

export default GraphBrowser;
